INSERT INTO `Text` (`Ar`,`Tr`,`En`) VALUES ('Պռօսխումէ։','Bṙoskhoome:','Be attentive.');
INSERT INTO `Text` (`Ar`,`Tr`,`En`) VALUES ('Եւ սարկաւագն բարձրացուցեալ զԱւետարանն սուրբ դառնայ ի շուրջ խորանոյն, ','Yev sargavakn partzratsootsyal zAvedarann soorp taṙna i shoorch khoranooyn, ','One of the deacons, elevating the holy Gospel, goes around the altar.');
